package fibonacci.test

class App {
    val greeting: String
        get() {
            return "Hello World!"
        }
}

fun main(args: Array<String>) {
    var n = 10
    var t1 = 0
    var t2 = 1

    print("First $n terms: ")

    for (i in 1..n) {

        val sum = t1 + t2
        t1 = t2
        t2 = sum
        println("fibonacci($n) = $sum ")
    }
}
